text:
.tc-sidebar-scrollable {
	position: fixed;
	top: 0px;
	left: 0;
	bottom: 0;
	margin: 0 0 0 10px;
	padding: 70px 0 28px 40px;
}
.tc-story-river {
	left: 340px;
	background: #F4F4F4;
	width: calc(100% - 400px);
}
<$reveal state="$:/state/sidebar" type="match" text="no">
.tc-story-river {
	left: 0px;
        width:100%;
}
</$reveal>
